import os
import requests
from bs4 import BeautifulSoup
import re

# Function to clean up the extracted text
def clean_text(text):
    # Remove extra whitespaces and newlines
    cleaned_text = re.sub(r"\s+", " ", text).strip()
    return cleaned_text

# Function to check if a tag contains unwanted content
def is_unwanted_tag(tag):
    # Define patterns to match unwanted content
    unwanted_patterns = [
        r"(^header$|^nav$|^footer$|^aside$|^script$|^style$)",
        r"(.*ad.*|.*banner.*|.*promo.*)",
        r"^(ul|ol|li)$"  # Exclude list elements
    ]
    for pattern in unwanted_patterns:
        if re.match(pattern, tag.name, re.IGNORECASE):
            return True
    return False

# Function to scrape text from an article
def scrape_article(url):
    response = requests.get(url)
    if response.status_code == 200:
        soup = BeautifulSoup(response.content, 'html.parser')

        # Find all the tags within the article content that are not unwanted
        content_tags = [tag for tag in soup.find_all(recursive=False) if not is_unwanted_tag(tag)]

        # Extract the text from the selected tags and clean it up
        extracted_text = [clean_text(tag.get_text(strip=True)) for tag in content_tags if tag.get_text(strip=True)]

        return " ".join(extracted_text)

    else:
        print(f"Failed to fetch the article. Status code: {response.status_code}")
        return None

