folder_path = "/content/documents"
os.makedirs(folder_path, exist_ok=True)

# Scrape text from each article and store them
for i, url in enumerate(article_urls):
    print(f"Scraping article {i+1}...")
    article_text = scrape_article(url)
    if article_text:
        # Create a unique filename based on the article index
        filename = f"article_{i}.txt"
        file_path = os.path.join(folder_path, filename)
        with open(file_path, "w", encoding="utf-8") as file:
            file.write(article_text)
            print(f"Article {i+1} stored successfully.")
    else:
        print(f"Failed to scrape article {i+1}.")

